$(function () {
	$("#send").click(function () {
       var message = $("#message").val();
       var author = $("#author").val();
       var task = $("#task").val();
       var name = $("#name").val();
       var d = new Date();
       var day = d.getDate();
       var mon = d.getMonth();
       var year = d.getFullYear();
       var hour = d.getHours();
       var min = d.getMinutes();
       var sec = d.getSeconds();
       var date = day + "." + mon + "." + year + " " + hour + ":" + min + ":" + sec;

       $.ajax({
         type: "POST",
         url: "/Comment/SendMessage",
         data: {"PersonId": author,"Text": message, "TaskId": task, "AddDate":date},
         cache: false,
         success: function(response){
             var messageResp = new Array('Ваше сообщение отправлено','Сообщение не отправлено Ошибка базы данных','Нельзя отправлять пустые сообщения');
             var resultStat = messageResp[Number(response)];
             if(response == 0){
                $("#message").val("");
             }
             $("#commentBlock").append("<div style='border:2px solid lightgrey; width: 90%;  margin: 20px auto; padding: 0px 20px; white-space: pre-wrap;'>"
				+ "<p>Автор: " + "<strong>" + name + "</strong></p><p>" + message + "</p><p>Дата создания: <strong>"
				+ date + "</strong></p><div> ");
             }
          });
          return false;                                                         
   });
});
